import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import './ProductCard.css'; // Pastikan file CSS ini ada dan diimpor

function ProductCard({ product }) {
  if (!product) {
    return null;
  }

  const handleAddToCartOnCard = (e) => {
    e.preventDefault(); // Mencegah navigasi jika tombol ada di dalam Link (meskipun di sini kita pisah)
    e.stopPropagation(); // Mencegah event bubbling ke Link jika ada
    console.log(`(Dari Kartu) Tambah ke keranjang: ${product.name}`);
    alert(`${product.name} ditambahkan ke keranjang! (Fungsi dummy)`);
    // Logika penambahan ke keranjang akan diimplementasikan nanti
  };

  return (
    <div className="product-card">
      <Link to={`/product/${product.id}`} className="product-card-clickable-area">
        <img src={product.image} alt={product.name} className="product-image" />
        <h3 className="product-name">{product.name}</h3>
      </Link>
      <div className="product-info">
        <p className="product-category">Kategori: {product.category}</p>
        <p className="product-price">
          Rp{product.price.toLocaleString('id-ID')}
        </p>
      </div>
      <button onClick={handleAddToCartOnCard} className="product-card-add-to-cart-button">
        ðŸ›’ Tambah
      </button>
    </div>
  );
}

ProductCard.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    category: PropTypes.string.isRequired,
    image: PropTypes.oneOfType([PropTypes.string, PropTypes.object]).isRequired,
    // specs: PropTypes.oneOfType([PropTypes.string, PropTypes.object]), // Baris ini masih bisa dikomentari jika specs tidak digunakan di kartu
  }).isRequired,
};

export default ProductCard;
